package vendor.bean;

public class Vendor {
	/**
	 * @author aditi
	 * @classname Vendor
	 * @creation date 17 july,2015
	 */
	private int vendorNo;
	private String vendorName;
	private long phnNum1;
	private long phnNum2;
	private String address1;
	private String address2;
	private String pincode;
	private String city;
	private String state;
	private String landmark;
	private String dirToStore;
	private String status;
	public Vendor(String vendorName, long phnNum1,
			long phnNum2, String address1, String address2, String pincode,
			String city, String state, String landmark, String dirToStore,
			String status) {
		super();
		this.vendorNo = vendorNo;
		this.vendorName = vendorName;
		this.phnNum1 = phnNum1;
		this.phnNum2 = phnNum2;
		this.address1 = address1;
		this.address2 = address2;
		this.pincode = pincode;
		this.city = city;
		this.state = state;
		this.landmark = landmark;
		this.dirToStore = dirToStore;
		this.status = status;
	}
	public Vendor() {
		// TODO Auto-generated constructor stub
	}
	public int getVendorNo() {
		return vendorNo;
	}
	public void setVendorNo(int vendorNo) {
		this.vendorNo = vendorNo;
	}
	public String getVendorName() {
		return vendorName;
	}
	public void setVendorName(String vendorName) {
		this.vendorName = vendorName;
	}
	public double getPhnNum1() {
		return phnNum1;
	}
	public void setPhnNum1(long phnNum1) {
		this.phnNum1 = phnNum1;
	}
	public double getPhnNum2() {
		return phnNum2;
	}
	public void setPhnNum2(long phnNum2) {
		this.phnNum2 = phnNum2;
	}
	public String getAddress1() {
		return address1;
	}
	public void setAddress1(String address1) {
		this.address1 = address1;
	}
	public String getAddress2() {
		return address2;
	}
	public void setAddress2(String address2) {
		this.address2 = address2;
	}
	public String getPincode() {
		return pincode;
	}
	public void setPincode(String pincode) {
		this.pincode = pincode;
	}
	public String getCity() {
		return city;
	}
	public void setCity(String city) {
		this.city = city;
	}
	public String getState() {
		return state;
	}
	public void setState(String state) {
		this.state = state;
	}
	public String getLandmark() {
		return landmark;
	}
	public void setLandmark(String landmark) {
		this.landmark = landmark;
	}
	public String getDirToStore() {
		return dirToStore;
	}
	public void setDirToStore(String dirToStore) {
		this.dirToStore = dirToStore;
	}
	public String getStatus() {
		return status;
	}
	public void setStatus(String status) {
		this.status = status;
	}
}